module.exports = a = async ({params}) => {
	// Retrieve the password hash removing the first 5 characters. 
	//The response from the API call is a list of hashes along with a numeric value which is the number of breaches.
	// ie: 0018A45C4DEF81644B53AB8F969B88D65:1
	//     001HBDKEHBDK38D84LBJ39ALIH933938D:389
	var hashSubstring = params.password_hash.substring(5, params.password_hash.length)

	// Split list into loopable format
	var pwnedResponseList = params.pwned_response.split('\r\n')

	// Set breachCount to 0 by default
	var breachedCount = 0;

	// Loop though the response list and find the related item (if any) and retrieve the breach count.
	for (let i = 0; i < pwnedResponseList.length; i++) {
		// select current item in iteration
		var pwnedItem = pwnedResponseList[i];
		// separate hash from breach count - ie: (left half)001HBDKEHBDK38D84LBJ39ALIH933938D:389(right half)
		var pwnedSplitList = pwnedItem.split(':')

		// Check if the left half of the current item equals the hash suffix of the password.
		if (pwnedSplitList[0].toLowerCase() == hashSubstring.toLowerCase()) {
			// Password was found in a breach, set breachCount value to equal the right half of current item.
			breachedCount = pwnedSplitList[1];
			break;
		}

	}

	return {'breachedCount': breachedCount}
}
